import { useState } from "react";

function Unilomake() {

  const [uni, setValues] = useState({
    maara: '',
    pvm: '',
    laatu: '',
    lisatietoja: '',
  });

  const [viesti, setViesti] = useState('');

  // Funktio painikkeen painallukselle
  const lisaaUni = (e) => {
    //e.preventDefault();
    setViesti('Uni lisätty tietokantaan');
    setValues({
      maara: '',
      pvm: '',
      laatu: '',
      lisatietoja: '',
    });
  }

  return (
    <div>
      <form>
      <label htmlFor='maara'>Unen määrä </label>
      <input type='text' name='maara' value={uni.maara} onChange={(e) => muuta(e)} /><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' value={uni.pvm} onChange={(e) => muuta(e)} /><br />

        <label htmlFor='laatu'>Unenluniaatu: </label>
        <input type='text' name='laatu' value={uni.laatu} onChange={(e) => muuta(e)} /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' value={uni.lisatietoja} onChange={(e) => muuta(e)} /><br />

        <input type='button' value='Lisää' onClick={(e) => lisaaUni(e)} />
      </form>
      <p>{viesti}</p>
    </div>
  );
}
export default Unilomake;
