import { useState } from "react";

function Ruokalomake () {

    const [ruoka, setValues] = useState({
        nimi: '',
        pvm: '',
        aika: '',
        lisatietoja: '',
    });

    const [viesti, setViesti] = useState('');

  // Funktio painikkeen painallukselle
  const lisaaUni = (e) => {
    //e.preventDefault();
    setViesti('Ruoka lisätty tietokantaan');
    setValues({
        nimi: '',
        pvm: '',
        aika: '',
        lisatietoja: '',
    });
  }
  return (
    <div>
      <form>
        <label htmlFor='maara'>Määrä </label>
        <input type='text' name='maara' value={uni.maara} /><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' value={uni.pvm} /><br />

        <label htmlFor='laatu'>Laatu: </label>
        <input type='text' name='laatu' value={uni.laatu} /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' value={uni.lisatietoja} /><br />


        <input type='button' value='Lisää' onClick={(e) => lisaaUni(e)} />
      </form>
      <p>{viesti}</p>
    </div>
  );
}
export default Ruokalomake;

