import { useState } from "react";

function Ruokalomake () {

    const [ruoka, setValues] = useState({
        nimi: '',
        pvm: '',
        aika: '',
        lisatietoja: '',
    });

    const [viesti, setViesti] = useState('');

  // Funktio painikkeen painallukselle
  const lisaaRuoka = (e) => {
    //e.preventDefault();
    setViesti('Ruoka lisätty tietokantaan');
    setValues({
        nimi: '',
        pvm: '',
        aika: '',
        lisatietoja: '',
    });
  }
  return (
    <div>
      <form>
        <label htmlFor='nimi'>Määrä </label>
        <input type='text' name='nimi' value={ruoka.maara} /><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' value={ruoka.pvm} /><br />

        <label htmlFor='aika'>Aika: </label>
        <input type='text' name='aika' value={ruoka.aika} /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' value={ruoka.lisatietoja} /><br />


        <input type='button' value='Lisää' onClick={(e) => lisaaRuoka(e)} />
      </form>
      <p>{viesti}</p>
    </div>
  );
}
export default Ruokalomake;

