import { useState } from "react";
function Muokkaaunta() {
  
const [uni, setValues] = useState({
  maara: '234',
  pvm: '123',
  laatu: '123',
  lisatietoja: '123',
});


const [viesti, setViesti] = useState('');

// Funktio, jolla muutetaan tilaa
const muuta = (e) => {
  setViesti('Muokattu');
  setValues(
    {
      ...uni,
      [e.target.name]: e.target.value
    }
  );
};

  return (
    <div>
      <form>
        <label htmlFor='maara'>Unenmäärä </label>
        <input type='text' name='maara' value={uni.maara} /><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' value={uni.pvm} /><br />

        <label htmlFor='laatu'>Laatu: </label>
        <input type='text' name='laatu' value={uni.laatu} /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' value={uni.lisatietoja} /><br />

        <input type='button' value='Muuta' onClick={(e) => muuta(e)} />
      </form>
    </div>
  );
}
export default Muokkaaunta;
