import React, { useState} from 'react';
import Box from '@mui/material/Box';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemIcon from '@mui/material/ListItemIcon';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import CreateIcon from '@mui/icons-material/Create';
import MenuIcon from '@mui/icons-material/Menu';
import PersonIcon from '@mui/icons-material/Person';
import SettingsIcon from '@mui/icons-material/Settings';
import CloudQueueIcon from '@mui/icons-material/CloudQueue';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import ListIcon from '@mui/icons-material/List';

function MenuMUI () {

 const [anchorNavi, setOpenNavi] = useState(null);
 const [anchorRuoka, setOpenRuoka] = useState(null);

 const menuOpen = (e) => {
  setOpenNavi(e.currentTarget);
 };


 const RuokaOpen = (e) => {
    setOpenRuoka(e.currentTarget);
   };

 const menuClose = () => {
  setOpenPerson(null);
  setOpenNavi(null);
  setOpenRuoka(null);
 }
  
 const menu =
    <Menu
      anchorEl={anchorNavi}
      open={Boolean(anchorNavi)}
      onClose={menuClose}
      anchorOrigin={
        { vertical: 'top', horizontal: 'right' }
      }
    >
      <MenuItem onClick={menuClose}>
        <ListItemIcon><CreateIcon /></ListItemIcon>
        <ListItemText primary='Lis채채' />
      </MenuItem>
      <MenuItem onClick={menuClose}>
        <ListItemIcon><ListIcon /></ListItemIcon>
        <ListItemText primary='Listaa' />
      </MenuItem>
      <MenuItem onClick={menuClose}>
        <ListItemIcon><CloudQueueIcon /></ListItemIcon>
        <ListItemText primary='S채채' />
      </MenuItem>
    </Menu>;


  return (
    <Box>
      <AppBar position='static'>
        <Toolbar>
          <IconButton onClick={ menuOpen } color='inherit'>
            <MenuIcon />
          </IconButton>
          { menu }
          
          <Typography variant='h5' sx={ {flexGrow: 1, textAlign: 'center'} }>Matkat</Typography>
          
          <IconButton onClick={ personOpen } color='inherit'>
            <AccountCircleIcon />
          </IconButton>
          { person }
          
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default  MenuMUI;
