import { useState } from "react";

function Unilomake() {

  const [uni, setValues] = useState({
    maara: '',
    pvm: '',
    laatu: '',
    lisatietoja: '',
  });

  const [viesti, setViesti] = useState('');

  // Funktio painikkeen painallukselle
  const lisaaUni = (e) => {
    //e.preventDefault();
    setViesti('Uni lisätty tietokantaan');
    setValues({
      maara: '',
      pvm: '',
      laatu: '',
      lisatietoja: '',
    });
  }
  return (
    <div>
      <form>
        <label htmlFor='maara'>Määrä </label>
        <input type='text' name='maara' value={maara} /><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' value={pvm} /><br />

        <label htmlFor='laatu'>Laatu: </label>
        <input type='text' name='laatu' value={laatu} /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' value={lisatietoja} /><br />


        <input type='button' value='Lisää' onClick={(e) => lisaaUni(e)} />
      </form>
      <p>{viesti}</p>
    </div>
  );
}
export default Unilomake;
