import React, { useState} from 'react';
import Box from '@mui/material/Box';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ListItemText from '@mui/material/ListItemText';
import LunchDiningIcon from '@mui/icons-material/LunchDining';
import NotificationsPausedIcon from '@mui/icons-material/NotificationsPaused';
import Button from '@mui/material/Button';


function MenuMUI2 () {

 const [anchorFood, setOpenFood] = useState(null);
 const [open, setOpen] = useState(false);

 const handleOpen = (event) => {
  setAnchorEl(event.currentTarget);
  setOpen(true);
};
const handleClose = (e) => {
  if (e.currentTarget.localName !== "ul") {
    const menu = document.getElementById("simple-menu").children[2];
    const menuBoundary = {
      left: menu.offsetLeft,
      top: e.currentTarget.offsetTop + e.currentTarget.offsetHeight,
      right: menu.offsetLeft + menu.offsetHeight,
      bottom: menu.offsetTop + menu.offsetHeight
    };
    if (
      e.clientX >= menuBoundary.left &&
      e.clientX <= menuBoundary.right &&
      e.clientY <= menuBoundary.bottom &&
      e.clientY >= menuBoundary.top
    ) {
      return;
    }
  }

  setOpen(false);
};
 const foodOpen = (e) => {
  setOpenFood(e.currentTarget );
 };

 const menuClose = () => {
    setOpenFood(null);
 }
  


const food =
<Menu 
  anchorEl={ anchorFood }
  open={ Boolean(anchorFood) }
  onClose={menuClose}
  onMouseLeave={menuClose}
>
  <MenuItem onClick={ menuClose }>
    <ListItemText primary='Ruokalista' />
  </MenuItem>
  <MenuItem onClick={ menuClose }>
    <ListItemText primary='Lisää ruoka' />
    </MenuItem>
    
</Menu>;


  return (
    <Box>
      <AppBar sx={{backgroundColor: '#45484e',color: '#FAD22B' }} position='static'>
        <Toolbar >

          <Button onMouseEnter={foodOpen} variant="outlined" color='inherit'><LunchDiningIcon /> Ruoka</Button>
          { food }

          <Box flexGrow={0.05} />
          

          
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default  MenuMUI2;
