import { useState } from "react";

function Muokkaaruokaa() {

  const [ruoka, setValues] = useState({
    nimi: '123',
    pvm: '123',
    aika: '123',
    lisatietoja: '123',
  });


  const [viesti, setViesti] = useState('');

  // Funktio painikkeen painallukselle
  const muokkaa = (e) => {
    //e.preventDefault();
    setViesti('Ruoka lisätty tietokantaan');
    setValues({
      nimi: '',
      pvm: '',
      aika: '',
      lisatietoja: '',
    });
  }
  return (
    <div>
      <form>
        <label htmlFor='nimi'>Ruoan nimi: </label>
        <input type='text' name='nimi' value={ruoka.lisatietoja}/><br />

        <label htmlFor='pvm'>Päivämäärä: </label>
        <input type='text' name='pvm' /><br />

        <label htmlFor='aika'>Aika: </label>
        <input type='text' name='aika' /><br />

        <label htmlFor='lisatietoja'>Lisätietoja: </label>
        <input type='text' name='lisatietoja' /><br />


        <input type='button' value='Lisää' onClick={(e) => muokkaa(e)} />
      </form>
      <p>{viesti}</p>
    </div>
  );
}
export default Muokkaaruokaa;
