import React, { useState} from 'react';
import Box from '@mui/material/Box';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ListItemText from '@mui/material/ListItemText';
import LunchDiningIcon from '@mui/icons-material/LunchDining';
import NotificationsPausedIcon from '@mui/icons-material/NotificationsPaused';
import Button from '@mui/material/Button';

function MenuMUI () {

 const [anchorFood, setOpenFood] = useState(null);
 const [anchorSleep, setOpenSleep] = useState(null);

 const foodOpen = (e) => {
  setOpenFood(e.currentTarget );
  setOpenSleep(null);
 };

 const sleepOpen = (e) => {
  setOpenSleep(e.currentTarget);
  setOpenFood(null);
 };

 const menuClose = () => {
    setOpenFood(null);
    setOpenSleep(null);
 }
 

const food =
<Menu 
  anchorEl={ anchorFood }
  open={ Boolean(anchorFood) }
  onClose={ menuClose }
>
  <MenuItem onClick={ menuClose }>
    <ListItemText primary='Ruokalista' />
  </MenuItem>
  <MenuItem onClick={ menuClose }>
    <ListItemText primary='Lis채채 ruoka' />
    </MenuItem>
</Menu>;

  const sleep =
    <Menu
      anchorEl={ anchorSleep }
      open={ Boolean(anchorSleep) }
      onClose={ menuClose }
    >
      <MenuItem onClick={ menuClose }
      >
        <ListItemText primary='Unilista' />
      </MenuItem>
      <MenuItem onClick={ menuClose }>
        <ListItemText primary='Lis채채 uni' />
        </MenuItem>
    </Menu>;

  return (
    <Box>
      <AppBar sx={{backgroundColor: '#45484e',color: '#FAD22B' }} position='static'>
        <Toolbar >

          <Button onMouseEnter={foodOpen} variant="outlined" color='inherit'><LunchDiningIcon /> Ruoka</Button>
          { food }

          <Box flexGrow={0.05} />
          
          <Button onMouseEnter={sleepOpen}variant="outlined" color='inherit'><NotificationsPausedIcon /> Uni</Button>
          { sleep }

          
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default  MenuMUI;
